function ocultaHeader(){document.querySelector(".header").classList.add("header--is-hidden")}function exibeHeader(){document.querySelector(".header").classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY>e?ocultaHeader:exibeHeader)()})});