function ocultaHeader(){document.querySelector(".header").classList.add("header--is-hidden")}function exibeHeader(){document.querySelector(".header").classList.remove("header--is-hidden")}function abreFecha(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector(".hero").clientHeight;var t=document.querySelectorAll("[data-faq-question]");window.addEventListener("scroll",function(){(window.scrollY>e?ocultaHeader:exibeHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",abreFecha)});